    public void loadNextDataFromApi(int offset) {
        // Send an API request to retrieve appropriate paginated data
        //  --> Send the request including an offset value (i.e `page`) as a query parameter.
        //  --> Deserialize and construct new model objects from the API response
        //  --> Append the new data objects to the existing set of items inside the array of items
        //  --> Notify the adapter of the new items made with `notifyItemRangeInserted()`
        String requestNextResourceURL = Api.URL_GET_RECOMMEND_COOKIE_USER + "/" + offset + "/5";
        GetNextStreamsTask task = new GetNextStreamsTask(new AsyncResponse() {
            @Override
            public void processFinish(String output) {
                ApiResponse response = JsonHelper.deserialize(output , ApiResponse.class);
                if(response !=null && response.statusCode == 200){
                    List<Map<String, Object>> streams = (List<Map<String, Object>>) response.data;
                    for(Map<String, Object> obj : streams){
                        LiveStream s = LiveStreamHelper.parse(obj);
                        if(s == null) continue;
                        listStream.add(new StreamViewModel(s.getTitle() ,
                                s.getOwner().username ,
                                s.getOwner().avatar , s.getThumbnail()));
                    }
                    if(streams !=null && streams.size() > 0)
                        adapter.notifyItemRangeChanged(offset >  0 ? (offset*5 -1) : 0 , streams.size() );
                }
            }
        });
        task.execute(requestNextResourceURL);
    }

