    // AsyncTask to get the genre list
    private class GetNextStreamsTask extends AsyncTask<String, Void, String> {
        public AsyncResponse asyncResponse = null;

        public GetNextStreamsTask(AsyncResponse asyncResponse) {
            this.asyncResponse = asyncResponse;
        }

        @Override
        protected String doInBackground(String... urls) {
            String url = urls[0];
            OkHttpClient client = new OkHttpClient();
            Request request = new Request.Builder().url(url).build();

            try (Response response = client.newCall(request).execute()) {
                String rs = response.body().string();
                return rs;
            } catch (IOException e) {
                e.printStackTrace();
                return null;
            }
        }

        @Override
        protected void onPostExecute(String s) {
            super.onPostExecute(s);
            if(asyncResponse != null) asyncResponse.processFinish(s);
        }
    }
